import React, { useContext } from "react";
import MovieContext from "../store/MovieContext";
import classes from "./Movie.module.css";

const Movie = (props) => {
  const movieCtx = useContext(MovieContext);
  const url = `https://used-react-fetch-default-rtdb.firebaseio.com/movies`;

  const deleteBtnHandler = async (id) => {
    try {
      const responseDelete = await fetch(`${url}/${id}.json`, {
        method: "DELETE",
      });

      const response = await fetch(`${url}.json`);
      if (!response.ok || !responseDelete.ok)
        throw new Error("Something went wrong");

      const data = await response.json();

      const allMoviesData = [];
      for (let key in data) {
        allMoviesData.unshift({
          id: key,
          title: data[key].title,
          releaseDate: data[key].releaseDate,
          openingText: data[key].openingText,
        });
      }
      // console.log(allMoviesData);.

      movieCtx.addMovie(allMoviesData);
    } catch (error) {
      movieCtx.errorHelper(error.message);
    }
  };

  return (
    <li className={classes.movie}>
      <h2>{props.title}</h2>
      <h3>{props.releaseDate}</h3>
      <p>{props.openingText}</p>
      <button
        type="button"
        className={classes['delete-btn']}
        onClick={deleteBtnHandler.bind(null, props.id)}
      >
        Delete
      </button>
    </li>
  );
};

export default Movie;
