import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [moviesData, setMoviesData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isCancel, setIsCancel] = useState(false);

  const fetchApiMoviesHandler = () => {
    // setIsCancel(false);
    setIsLoading(true);
    const getMoviesHalper = async () => {
      return await fetch("https://swapi.dev/api/films/");
    };
    console.log(isCancel);
    getMoviesHalper()
      .then((response) => {
        if (response.ok) return response.json();
        else {
          setTimeout(() => {
            setIsCancel(false);
          }, 5000);
        }
      })
      .then((data) => {
        const mappedMoviesData = data.results.map((data) => ({
          id: data.episode_id,
          title: data.title,
          releaseDate: data.release_date,
          openingText: data.opening_crawl,
        }));
        setMoviesData(mappedMoviesData);
        setIsLoading(false);
        setIsCancel(false);
      })
      .catch((error) => console.log(error));


  };

  const cancelButtonHelper = () => {
    setIsCancel(true);
    setIsLoading(false);
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchApiMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && <MoviesList movies={moviesData} />}
        {!isLoading && moviesData.length === 0 && <p>Not found moviesData</p>}
        {isLoading && (
          <div>
            <p>Loading...</p>
            <button type="button" onClick={cancelButtonHelper}>
              Cancel
            </button>
          </div>
        )}
      </section>
    </React.Fragment>
  );
}

export default App;
