import React, { useState, useEffect, useCallback, useContext } from "react";
import MovieContext from "./store/MovieContext";
import MoviesList from "./components/MoviesList";
import "./App.css";
import Form from "./components/Form/Form";

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [isError, setIsError] = useState(null);
  const movieCtx = useContext(MovieContext);

  const fetchApiMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setIsError(null);
    try {
    const response = await fetch(
      "https://used-react-fetch-default-rtdb.firebaseio.com/movies.json"
    );
    if (!response.ok) throw new Error("Something went wrong ...Retrying");

    const data = await response.json();
    const allMoviesData = [];
    for (let key in data) {
      allMoviesData.push({
        id: key,
        title: data[key].title,
        releaseDate: data[key].releaseDate,
        openingText: data[key].openingText,
      });
    }
    console.log(allMoviesData);
    // const mappedMoviesData = data.results.map((data) => ({
    //   id: uuidv4(),
    //   title: data.title,
    //   releaseDate: data.release_date,
    //   openingText: data.opening_crawl,
    // }));
    setMoviesData(allMoviesData);
    // movieCtx.addMovie(mappedMoviesData);
    setIsLoading(false);
    }
    catch(error) {
      setIsError(error.messages);
    }
  }, []);

  useEffect(() => {
    fetchApiMoviesHandler();
  }, [fetchApiMoviesHandler]);

  return (
    <React.Fragment>
      <section>
        <Form />
      </section>
      <section>
        <button onClick={fetchApiMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && <MoviesList movies={moviesData}/>}
        {!isLoading && movieCtx.movies.length === 0 && (
          <p>Not found moviesData</p>
        )}
        {isLoading && <p>Loading...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
