import MovieContext from "./MovieContext";
import React, { useReducer } from "react";

const defaultState = {
  movies: [],
};
const movieReducer = (state, action) => {
  if (action.type === "ADD") {
    return {
      movies: [...action.movie, ...state.movies],
    };
  } else if (action.type === "DELETE") {
    return {
      movies: state.movies.filter((movie) => movie.id !== action.id),
    };
  } else return defaultState;
};

const MovieProvider = (props) => {
  const [movieState, dispatchMovie] = useReducer(movieReducer, defaultState);

  const addMovieHelper = (movie) => {
    dispatchMovie({ type: "ADD", movie: movie });
  };

  const deleteMovieHelper = (id) => {
    dispatchMovie({ type: "DELETE", id: id });
  };

  return (
    <MovieContext.Provider
      value={{
        movies: movieState.movies,
        addMovie: addMovieHelper,
        deleteMovie: deleteMovieHelper,
      }}
    >
      {props.children}
    </MovieContext.Provider>
  );
};

export default MovieProvider;
