import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [moviesData, setMoviesData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const fetchApiMoviesHandler = () => {
    setIsLoading(true);
    try {
      const getMoviesHalper = async () => {
        const response = await fetch("https://swapi.dev/api/filmv");
        if (!response.ok) throw Error("Something went wrong ...Retrying");
        return await response.json();
      };

      getMoviesHalper()
        .then((data) => {
          const mappedMoviesData = data.results.map((data) => ({
            id: data.episode_id,
            title: data.title,
            releaseDate: data.release_date,
            openingText: data.opening_crawl,
          }));
          setMoviesData(mappedMoviesData);
          setIsLoading(false);
        })

    } catch (error) {
      console.log(error);
    }
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchApiMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && <MoviesList movies={moviesData} />}
        {!isLoading && moviesData.length === 0 && <p>Not found moviesData</p>}
        {isLoading && <p>Loading...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
