import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [moviesData, setMoviesData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [cancelRetrying, setCancelRetying] = useState(false);

  const fetchApiMoviesHandler = () => {
    setIsLoading(true);
    const getMoviesHalper = async () => {
      const response = await fetch("https://swapi.dev/api/films/");
      if (!response.ok) {
        setCancelRetying(true);
        throw Error("Something went wrong ...Retrying");
      } else {
        setCancelRetying(false);
        return await response.json();
      }
    };

    setTimeout(() => {
      if (!cancelRetrying)
        getMoviesHalper()
          .then((data) => {
            const mappedMoviesData = data.results.map((data) => ({
              id: data.episode_id,
              title: data.title,
              releaseDate: data.release_date,
              openingText: data.opening_crawl,
            }));
            setMoviesData(mappedMoviesData);
            setIsLoading(false);
          })
          .catch((error) => console.log(error));
    }, 5000);
  };

  const cancelButtonHelper = () => {
    setCancelRetying(true);
    setIsLoading(false);
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchApiMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && <MoviesList movies={moviesData} />}
        {!isLoading && moviesData.length === 0 && <p>Not found moviesData</p>}
        {isLoading && (
          <div>
            <p>Loading...</p>
            <button type="button" onClick={cancelButtonHelper}>
              Cancel
            </button>
          </div>
        )}
      </section>
    </React.Fragment>
  );
}

export default App;
