import React, { useState, useEffect, useCallback, useContext } from "react";
import MovieContext from "./store/MovieContext";
import MoviesList from "./components/MoviesList";
import "./App.css";
import Form from "./components/Form/Form";

function App() {
  const [isLoading, setIsLoading] = useState(false);

  const movieCtx = useContext(MovieContext);

  const fetchApiMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    movieCtx.errorHelper(null);
    try {
      const response = await fetch(
        "https://used-react-fetch-default-rtdb.firebaseio.com/movies.json"
      );
      if (!response.ok) throw new Error("Something went wrong");

      const data = await response.json();

      const allMoviesData = [];
      for (let key in data) {
        allMoviesData.unshift({
          id: key,
          title: data[key].title,
          releaseDate: data[key].releaseDate,
          openingText: data[key].openingText,
        });
      }
      // console.log(allMoviesData);.

      movieCtx.addMovie(allMoviesData);
      setIsLoading(false);
    } catch (error) {
      movieCtx.errorHelper(error.message);
    }
  }, []);
  console.log(movieCtx.movies);

  useEffect(() => {
    fetchApiMoviesHandler();
  }, [fetchApiMoviesHandler]);

  return (
    <React.Fragment>
      <section>
        <Form />
      </section>
      <section>
        <button onClick={fetchApiMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && <MoviesList/>}
        {/* {!isLoading && movieCtx.movies.length === 0 && (
          <p>Not found moviesData</p>
        )} */}
        {!isLoading && movieCtx.error && <p>{movieCtx.error}</p>}
        {isLoading && <p>Loading...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
