import React from "react";

import classes from "./Movie.module.css";

const Movie = (props) => {
  const deleteBtnHandler = async (id) => {
    await fetch(
      `https://used-react-fetch-default-rtdb.firebaseio.com/movies.json/${props.id}`
    , {
      method:'DELETE',

    });
  };

  return (
    <li className={classes.movie}>
      <h2>{props.title}</h2>
      <h3>{props.releaseDate}</h3>
      <p>{props.openingText}</p>
      <button type="button" onClick={deleteBtnHandler.bind(null, props.id)}>
        Delete
      </button>
    </li>
  );
};

export default Movie;
